function beginGen(){
    if(window.document.getElementById("resetOnGen").checked){
        resetHTML("cityList");
    }
        genFrCityName(window.document.getElementById("nbGen").value);
}

function genFrCityName(nbCity){
    for(var i = 0; i < nbCity; i++){
        var nomVille = "";
        var debutChoisi = debut[Math.floor(Math.random() * debut.length)];
        var finChoisi = fin[Math.floor(Math.random() * fin.length)];
        
        if(debutChoisi.slice(-1) == "i" && finChoisi.slice(0,1) == "i"){
           debutChoisi = debutChoisi.slice(0,-1);
        }
        
        if(debutChoisi.slice(-1) == "o" && finChoisi.slice(0,1) == "o"){
           debutChoisi = debutChoisi.slice(0,-1);
        }
        
        if(debutChoisi.slice(-1) == "a" && finChoisi.slice(0,1) == "a"){
           debutChoisi = debutChoisi.slice(0,-1);
        }
        
        if(debutChoisi.slice(-1) == "t" && finChoisi.slice(0,1) == "v"){
           if(randomInt(1,2) == 2){
               debutChoisi = debutChoisi.slice(0,-1)
           }else{
               finChoisi = finChoisi.slice(1);
           }
        }
        
        if(debutChoisi.slice(-1) == "d" && finChoisi.slice(0,1) == "t"){
           if(randomInt(1,2) == 1){
               debutChoisi = debutChoisi.slice(0,-1)
           }else{
               finChoisi = finChoisi.slice(1);
           }
        }
        
        if(debutChoisi.slice(-1) == "d" && finChoisi.slice(0,2) == "ch"){
           if(randomInt(1,2) == 1){
               debutChoisi = debutChoisi.slice(0,-1)
           }else{
               finChoisi = finChoisi.slice(2);
           }
        }
        
        if(debutChoisi.slice(0,1) == "H" || debutChoisi.slice(0,1) == "A" || debutChoisi.slice(0,1) == "E" || debutChoisi.slice(0,1) == "I" || debutChoisi.slice(0,1) == "O" || debutChoisi.slice(0,1) == "U" || debutChoisi.slice(0,1) == "Y" || debutChoisi.slice(0,1) == "É"){
            if(randomInt(1,20) == 1){
                nomVille = "L'";
            }
        }else{
            if(randomInt(1,20) == 1){
                if(randomInt(1,2) == 1){
                    nomVille = "Le ";
                }else{
                    nomVille = "La ";
                }
            }
        }
        
        if(randomInt(1,10) == 1){
            if(randomInt(1,20) == 1){
                if(randomInt(1,15) == 1){
                    nomVille = "L'";
                }
                if(randomInt(1,2) == 1){
                    nomVille += "Haut-";
                }else{
                    nomVille += "Haute-";
                }
            }else{
                if(randomInt(1,2) == 1){
                    nomVille = "Saint-";
                }else{
                    nomVille = "Sainte-";
                }
            }
        }
        
        nomVille += debutChoisi;
        nomVille += finChoisi;
        
        if(randomInt(1,10) == 1){
            var deter = milieu[Math.floor(Math.random() * milieu.length)];
            nomVille += deter;
            nomVille += compose[Math.floor(Math.random() * compose.length)];
            if(deter == "-les-"){
                if(nomVille.slice(-1) != "s" && nomVille.slice(-1) != "x"){
                    if(nomVille.slice(-1) != "u"){
                        nomVille += "s";
                    }else{
                        nomVille += "x";
                    }
                }
            }
        }else if(randomInt(1,9) == 1){
            nomVille += "-";
            nomVille += compose[Math.floor(Math.random() * compose.length)];
        }
        
        nomVille = nomVille.toUpperCase();
        var outerPanel = document.createElement("div");
        outerPanel.className = "outerCityPanel";
        var panel = document.createElement("div");
        panel.className = "cityPanel";
        var cityName = document.createTextNode(nomVille);
        panel.appendChild(cityName);
        outerPanel.appendChild(panel);
        window.document.getElementById("cityList").appendChild(outerPanel);
    }
}